package com.example.pojochat.Model;public class User{    public String id;    public String username;    public String phone;    public String email;    public String password;    public String Image;    public User(String username, String phone, String email, String password, String image) {        this.username = username;        this.phone = phone;        this.email = email;        this.password = password;        Image = image;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getPhone() {        return phone;    }    public void setPhone(String phone) {        this.phone = phone;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getImage() {        return Image;    }    public void setImage(String image) {        Image = image;    }}